import React, { useEffect,useState } from 'react';
import axios from 'axios';
import { Link, navigate } from '@reach/router';

const OnePet = props =>{
    const [onePet,setOnePet] = useState({});
    useEffect(()=>{
        axios.get(`http://localhost:8000/api/pets/${props._id}`)
            .then(res=>setOnePet(res.data.pet))
    })

    const removePet = _id =>{
        axios.delete(`http://localhost:8000/api/pets/delete/${_id}`)
            .then(res=> navigate("/"))
            .catch(err => console.log(err))
    }

    return(
        <div>
            <Link to ="/" className="nav_bar"><h4>back to home</h4></Link>
            <button type="submit" className="btn btn-danger mb-3"  onClick={()=> removePet(onePet._id)}>Adopt {onePet.name}</button>
            <h2>Details about: {onePet.name}</h2>
            <div>
            <h4>Pet Type: {onePet.type}</h4>
            <h4>Descriptioin: {onePet.description}</h4>
            <h4>Skills:</h4>
            <ul>
                <li>{onePet.skill_1}</li>
                <li>{onePet.skill_2}</li>
                <li>{onePet.skill_3}</li>
            </ul>
            </div>
        </div>
    )
}

export default OnePet;
